mapper.update:
UPDATE RECIPE
SET RECIPEREADCOUNT=RECIPEREADCOUNT+1
WHERE RECIPESEQ=#{recipeSeq}

UPDATE RECIPE
SET RECIPERATING=(
    SELECT ROUND(AVG(RATINGSCORE), 2)
    FROM RATING
    GROUP BY DOCSSEQ
    HAVING DOCSSEQ=#{docsSeq}
    )
WHERE RECIPESEQ=#{docsSeq}

mapper.select:
SELECT * FROM RECIPE WHERE RECIPETITLE LIKE CONCAT('%', #{search}, '%') AND RECIPEBIGCATEGORY IN
#{item}
AND RECIPESMALLCATEGORY IN
#{item}
ORDER BY RECIPEREADCOUNT DESC
ORDER BY RECIPERATING DESC

SELECT RECIPETITLE, RECIPEREADCOUNT, RECIPERATING
FROM RECIPE
WHERE RECIPESEQ = #{recipeSeq}
